function deleteCustomer(e,t){confirm("Are you sure you want to delete this customer?")&&ajaxUpdate(e,{customerid:t},deleteCustomerResponse,"delete")}function deleteCustomerResponse(e,t){hideLoader(),"success"==t?"success"==e.type?$("#notify").notification({caption:e.message,type:e.type,sticky:!1,onhide:function(){RefreshLocation()}}):$("#notify").notification({caption:e.message,type:e.type,sticky:!0}):$("#notify").notification({caption:"Sorry, Unable to communicate with server. Please try again later.",type:"error",sticky:!0})}